package com.trycloud.step_definitions;import com.trycloud.pages.StatusPage;import com.trycloud.utilities.BrowserUtils;import io.cucumber.java.en.Then;import io.cucumber.java.en.When;import org.junit.Assert;import org.openqa.selenium.WebElement;public class Status_Step_Definitions {    StatusPage statusPage = new StatusPage();    @When("user clicks on Status Icon")    public void user_clicks_on_status_icon() {       statusPage.statusButton.click();    }    @Then("user should should be able to select any {string}")    public void user_should_should_be_able_to_select_any(String status) {        switch (status) {            case "Online":                statusPage.onlineIcon.click();                break;            case "Away":                statusPage.awayIcon.click();                break;            case "Do not disturb":                statusPage.doNotDisturbIcon.click();                break;            case "Invisible":                statusPage.invisibleIcon.click();                break;            default:                System.out.println("This status option is not available");        }        statusPage.setStatusMessageButton.click();        Assert.assertEquals(statusPage.statusButton.getText(),status);        }}